mapper.insert:
insert into car_list(
	c_num,  u_id,  start_point,  dest_point,  way_point,  c_price,
	 c_year,  c_month,  c_date,  c_hour,  c_minute, c_person
)
values(
	#{c_num}, #{u_id}, #{start_point}, #{dest_point}, #{way_point}, #{c_price}, #{c_year}, #{c_month}, #{c_date}, #{c_hour}, #{c_minute}

	, #{c_person}
)

mapper.update:
update car_list 
set c_person = c_person-1
where c_num = #{c_num}
