mapper.insert:
INSERT INTO declare(
	d_num, u_id, d_reported, d_big, d_small, d_state, d_content, d_file
) VALUES (
	#{d_num}, #{u_id}, #{d_reported}, #{d_big}, #{d_small}, 'wait', #{d_content}, #{d_file}
)

mapper.update:
update declare 
set d_state ='ACCEPT'
where d_num = #{d_num}

update declare 
set d_state ='REJECT'
where d_num = #{d_num}
