mapper.insert:
insert into
product_req(
pr_reqnum, u_id, p_num, pr_ox, pr_place, pr_date
)
values(
#{pr_reqnum}, #{u_id}, #{p_num},
'indicision',#{pr_place},#{pr_date}
)

mapper.update:
UPDATE product_req SET
pr_ox='accepted'
WHERE pr_reqnum = #{pr_reqnum}

UPDATE product_req SET
pr_ox='rejected'
WHERE pr_reqnum = #{pr_reqnum}

UPDATE product_req SET
pr_ox='canceled'
WHERE pr_reqnum = #{pr_reqnum}
